import React, { useState, useEffect } from "react";
import "./App.css";
import Draggable from "react-draggable";
import { v4 as uuidv4 } from "uuid";
var randomColor = require("randomcolor");

const [item, setItem] = useState(""); // this is the value of input field. Initialize as empty string
const [items, setItems] = useState(
  JSON.parse(localStorage, getItem("items")) || []
);

const keyPress = (event) => {
  let code = event.keyCode || event.which;
  if (code === 13) {
    newItem();
  }
};

const newItem = () => {
  if (item.trim() !== "") {
    // if input is not blank, create a new item object
    const newItem = {
      id: uuidv4(),
      item: item,
      color: randomColor({ luminosity: "light" }),
      defaultPos: { x: 100, y: 0 },
    };
    // add this new item object to the items array
    setItems((items) => [...items, newItem]);
    // reset item value to empty string
    setItem("");
  } else {
    alert("Enter a item please");
    setItem("");
  }
};

useEffect(() => {
  localStorage.setItem("items", JSON.stringify(items));
}, [items]);

<input
  value={item}
  onChange={(e) => setItem(e.target.value)}
  placeholder="Enter something..."
  onKeyPress={(e) => keyPress(e)}
/>;
<button onClick={newItem}>ENTER</button>;

function App() {
  return (
    <div>
      {items.map((item, index) => {
        return (
          <Draggable
            key={item.id}
            defaultPosition={item.defaultPos}
            onStop={(e, data) => {
              updatePos(data, index);
            }}
          ></Draggable>
        );
      })}
    </div>
  );
}

export default App;
